import React from "react";
import { Link } from 'react-router-dom';
import '../Header/Header.scss'
import {useSelector} from "react-redux";


export const Header = () => {

    const favorites = useSelector(state => state.characters.favorites);

    const numFavorites = favorites ? favorites.length : 0
    return (
        <div className="headerClass">
            <Link to="/rickandmorty" style={{textDecoration: 'none'}}>
                <h1 className="title">Rick And Morty Characters</h1>
            </Link>
            <Link to="/favcharacters" style={{textDecoration: 'none'}}>
                <p className="favorites">Favorite Characters ()</p>
            </Link>
        </div>
    );
}
